import React, { useState } from 'react';
import LabelStyle1 from './labels/LabelStyle1';

function ToggleButton({toggleButtonClassName}) {
  const [isStudent, setIsStudent] = useState(true);

  return (
    <div className={`flex justify-center ${toggleButtonClassName}`}>
      <LabelStyle1 labelText='Administration' labelClassName={`mr-[5px] ${isStudent ? 'text-red-700' : ''}`} />
      <div className="flex w-[50px] h-[24px]">
        <div className={`flex border rounded-2xl h-full w-full relative ${isStudent ? 'bg-blue-300' : 'bg-blue-500'}`}>
          <div className={`absolute w-[22px] h-[22px] bg-white border border-gray-300 rounded-full transition-transform duration-300 ${isStudent ? 'left-0' : 'right-0'}`} />
          <button onClick={() => setIsStudent(true)} className={`flex-1`} />
          <button onClick={() => setIsStudent(false)} className={`flex-1`} />
        </div>
      </div>
      <LabelStyle1 labelText='Student' labelClassName={`ml-[5px] ${!isStudent ? 'text-red-700' : ''}`} />
    </div>
  );
}

export default ToggleButton;
