import React, { useState } from "react";

function App() {
  const [isInRed, setIsInRed] = useState(true);

  // تغيير موقع المستطيل الرمادي عند النقر
  const handleToggle = () => {
    setIsInRed(!isInRed);
  };

  return (
    <div className="flex items-center justify-center h-screen">
      {/* المستطيل الأحمر والأزرق المتلاصقين */}
      <div className="flex overflow-hidden">
        <div className="relative w-32 h-32 bg-red-500 overflow-hidden">
          {isInRed && (
            <div className="absolute w-24 h-24 bg-gray-500 transition-transform duration-700 transform translate-x-0"></div>
          )}
        </div>

        <div className="relative w-32 h-32 bg-blue-500 overflow-hidden">
          {!isInRed && (
            <div className="absolute w-24 h-24 bg-gray-500 transition-transform duration-700 transform -translate-x-full"></div>
          )}
        </div>
      </div>

      {/* زر التبديل خارج المستطيلات */}
      <button
        onClick={handleToggle}
        className="p-2 bg-green-500 text-white rounded ml-4"
      >
        Toggle Move
      </button>
    </div>
  );
}

export default App;
