// SideBar.js
import React, { useState } from "react";
import CollapseButton from "./custom controls/buttons/CollapseButton"; // استيراد زر التبديل
import { ChevronLeftIcon, ChevronRightIcon, ChevronDownIcon, ChevronUpIcon ,DocumentIcon} from "@heroicons/react/24/solid";

export default function SideBar() {
  const [isOpen, setIsOpen] = useState(true);             // حالة الشريط الجانبي
  const [isSubmenuOpen, setIsSubmenuOpen] = useState(false); // حالة القائمة الفرعية

  const SidebarButton = () => setIsOpen(isOpen => !isOpen);           // دالة تبديل الشريط الجانبي
  const MenuButton = () => setIsSubmenuOpen(isSubmenuOpen => !isSubmenuOpen); // دالة تبديل القائمة الفرعية

  return (
    <div className={`relative flex flex-col h-screen ${isOpen ? "w-64" : "w-16"} bg-gray-800 text-white transition-width duration-300`}>
      {/* زر الشريط الجانبي */}
      <CollapseButton 
        ButtonIcon1={ChevronLeftIcon} 
        ButtonIcon2={ChevronRightIcon}
        isOpen={isOpen}
        SidebarButton={SidebarButton}
      />

      <ul className={`flex flex-col mt-4 space-y-2 ${!isOpen && "hidden"}`}>
        {/* زر لتبديل القائمة الفرعية باستخدام CollapseButton */}
        <li className="flex items-center justify-between px-4 py-2 cursor-pointer hover:bg-gray-600 rounded relative">
        <div className="flex items-center space-x-2">
            <DocumentIcon className="h-5 w-5 text-white" /> {/* أيقونة المستند */}
            <span>DocumentRequests</span>
          </div>
          <CollapseButton 
            ButtonIcon1={ChevronDownIcon} 
            ButtonIcon2={ChevronUpIcon}
            isOpen={isSubmenuOpen}
            SidebarButton={MenuButton}
          />
           

          {/* القائمة الفرعية العائمة بحركة سلسة */}
          <ul
            className={`absolute left-full top-0 ml-2 mt-1 bg-gray-700 shadow-lg rounded-lg w-48 overflow-hidden transition-max-height duration-300 ease-in-out ${
              isSubmenuOpen ? "max-h-96" : "max-h-0"
            }`}
          >
            <li className="hover:bg-gray-600 p-2 rounded"><a href="">Certificate Of Enrollment</a></li>
            <li className="hover:bg-gray-600 p-2 rounded"><a href="">Transcript Request</a></li>
            <li className="hover:bg-gray-600 p-2 rounded"><a href="">Parking Permit</a></li>
            <li className="hover:bg-gray-600 p-2 rounded"><a href="">Library Card</a></li>
            <li className="hover:bg-gray-600 p-2 rounded"><a href="">Internship Permit</a></li>
            <li className="hover:bg-gray-600 p-2 rounded"><a href="">Student ID Card</a></li>
            <li className="hover:bg-gray-600 p-2 rounded"><a href="">Block Academic Year</a></li>
          </ul>
        </li>
      </ul>
    </div>
  );
}
