import React, { useState } from 'react';

function ToggleButton() {
  const [isStudent, setIsStudent] = useState(true);

  return (
    <div className="flex bg-white w-[30px] h-[10px]">
      <div className="flex border rounded-lg h-full w-full">
        <div className='bg-red-500 rounded-full w-[10px] h-[10px]'/>
        <button onClick={() => setIsStudent(true)} className={`${isStudent ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'} flex-1 text-lg`}></button>
        <button onClick={() => setIsStudent(false)} className={`${!isStudent ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'} flex-1 text-lg`}></button>
      </div>
    </div>
  );
}

export default ToggleButton;
