// ToggleButton.js
import React from "react";
import { ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/24/solid"; // استيراد أيقونات السهم

export default function CollapseButton({  ButtonIcon1,ButtonIcon2,isOpen, SidebarButton }) { // تلقي isOpen و toggleSidebar كـ props
  // دالة تبديل القائمة الفرعية
  const MenuButton = () => setIsOpen((prev) => !prev);

  return (
    <button
      className="p-2 bg-gray-700 hover:bg-gray-600 focus:outline-none rounded" // ضبط تصميم الزر ليشبه الزر الأول
      onClick={ SidebarButton} // استدعاء دالة التبديل عند النقر
    >
      {isOpen ? (
        <ButtonIcon1 className="h-5 w-5 text-white" /> // أيقونة السهم للإغلاق
      ) : (
        <ButtonIcon2 className="h-5 w-5 text-white" /> // أيقونة السهم للفتح
      )}
    </button>
  );
}
