import React from "react";
import Login_SingUp_bg from "../Images/Login_SingUp_bg.png"
import Login_SingUp_image from "../Images/Login_Singup_image.jpg"
import Login from "../components/Login";
import SingUp from "../components/SingUp";
import { useState } from "react";


export default function Login_SingUp(){
    const [position, setPosition] = useState(0);

  const SingInButtonHandled = () => {
    setPosition(0);
  };

  const SingupButtonHandled = () => {
    setPosition(1);
  };
    return(
        <div className="flex items-center justify-center h-screen bg-cover bg-center" style={{ backgroundImage: `url(${Login_SingUp_bg})` }}>
  <div className="grid grid-cols-1 sm:grid-cols-2 w-[95%] md:w-[80%] lg:w-[60%] h-[500px] bg-white rounded-lg shadow-md relative">
    
    {/* عنصر الصورة */}
    <div
      className={`hidden sm:block absolute h-full w-full sm:w-1/2 bg-blue-500 shadow-lg transform transition-transform duration-500 ease-in-out ${
        position === 0 ? 'translate-x-0 rounded-l-lg' : 'translate-x-full rounded-r-lg'
      }`}
    >
      <img src={Login_SingUp_image} alt="Univ-Boumerdes-Image" className={`w-full h-full object-cover ${position === 0 ? 'rounded-l-lg' : 'rounded-r-lg'}`}/>
    </div>
    
    {/* مكونات تسجيل الدخول والتسجيل */}
    <SingUp SingInButtonHandled={SingInButtonHandled}/>
    <Login SingupButtonHandled={SingupButtonHandled}/>
    
  </div>
</div>

    );
}
