import React, { useState } from "react";
import { ChevronDownIcon, ChevronUpIcon } from "@heroicons/react/24/solid"; // استيراد أيقونات السهم

export default function DocumentRequests() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState("");

  const toggleList = () => {
    setIsOpen(prev => !prev);
  };

  const handleSelectOption = (option) => {
    setSelectedOption(option);
    setIsOpen(false);
  };

  return (
    <div className="p-4">
      {/* العنصر الرئيسي للنقر مع السهم */}
      <div 
        onClick={toggleList} 
        className="flex items-center cursor-pointer font-semibold text-blue-600"
      >
        Document Requests
        {/* السهم يتغير بناءً على حالة القائمة */}
        {isOpen ? (
          <ChevronUpIcon className="h-5 w-5 ml-2 text-blue-600" />
        ) : (
          <ChevronDownIcon className="h-5 w-5 ml-2 text-blue-600" />
        )}
      </div>

      {/* عرض الخيار المحدد */}
      {selectedOption && (
        <div className="mt-2 text-gray-700">الخيار المحدد: {selectedOption}</div>
      )}

      {/* القائمة الفرعية التي تظهر عند فتح القائمة */}
      {isOpen && (
        <ul className="mt-2 space-y-1 border border-gray-300 rounded p-2 bg-white shadow-md">
          <li 
            onClick={() => handleSelectOption("شهادة مدرسية")} 
            className="cursor-pointer hover:bg-gray-200 p-1 rounded"
          >
            شهادة مدرسية
          </li>
          <li 
            onClick={() => handleSelectOption("كشف علامات")} 
            className="cursor-pointer hover:bg-gray-200 p-1 rounded"
          >
            كشف علامات
          </li>
          <li 
            onClick={() => handleSelectOption("تصريح وقوف")} 
            className="cursor-pointer hover:bg-gray-200 p-1 rounded"
          >
            تصريح وقوف
          </li>
          <li 
            onClick={() => handleSelectOption("بطاقة مكتبة")} 
            className="cursor-pointer hover:bg-gray-200 p-1 rounded"
          >
            بطاقة مكتبة
          </li>
          <li 
            onClick={() => handleSelectOption("تصريح تدريب")} 
            className="cursor-pointer hover:bg-gray-200 p-1 rounded"
          >
            تصريح تدريب
          </li>
          <li 
            onClick={() => handleSelectOption("بطاقة تعريف الطالب")} 
            className="cursor-pointer hover:bg-gray-200 p-1 rounded"
          >
            بطاقة تعريف الطالب
          </li>
          <li 
            onClick={() => handleSelectOption("حظر السنة الدراسية")} 
            className="cursor-pointer hover:bg-gray-200 p-1 rounded"
          >
            حظر السنة الدراسية
          </li>
        </ul>
      )}
    </div>
  );
}
