import React, { useState } from "react";
import CollapseButton from "./custom controls/buttons/CollapseButton";
import { DocumentIcon } from "@heroicons/react/24/solid";

export default function SideBar() {
  const [isOpen, setIsOpen] = useState(true); // حالة الشريط الجانبي
  const [openList, setOpenList] = useState(""); // القائمة الفرعية المفتوحة

  const handleListClick = (listName) => {
    setOpenList((prevList) => (prevList === listName ? "" : listName));
  };

  const toggleSidebar = () => {
    if (isOpen) {
      setOpenList(""); // إغلاق جميع القوائم الفرعية عند غلق الشريط الجانبي
    }
    setIsOpen(!isOpen);
  };

  return (
    <div className={`relative flex flex-col h-screen ${isOpen ? "w-64" : "w-16"} bg-blue-500 text-white transition-width duration-300`}>
      {/* زر الشريط الجانبي */}
      <CollapseButton
        isOpen={isOpen}
        setIsOpen={toggleSidebar} // استخدام الدالة المخصصة
        Orientation={"horizontal"}
      />

      <ul className={`flex flex-col mt-4 space-y-2 ${!isOpen && "hidden"}`}>
        {/* عنصر DocumentRequests مع القائمة الفرعية */}
        <li className="flex items-center justify-between px-4 py-2 cursor-pointer hover:bg-blue-600 rounded relative">
          <div className="flex items-center space-x-2 text-lg font-semibold">
            <DocumentIcon className="h-5 w-5 text-white" />
            <span>DocumentRequests</span>
          </div>
          <CollapseButton
            isOpen={openList === "documentrequest"}
            setIsOpen={() => handleListClick("documentrequest")}
            Orientation={"vertical"}
          />
          <ul
            className={`absolute left-full top-0 ml-2 mt-1 bg-gray-700 shadow-lg rounded-lg w-48 overflow-hidden transition-max-height duration-300 ease-in-out ${
              openList === "documentrequest" ? "max-h-96" : "max-h-0"
            }`}
          >
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Certificate Of Enrollment</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Transcript Request</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Parking Permit</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Library Card</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Internship Permit</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Student ID Card</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Block Academic Year</a></li>
          </ul>
        </li>

        {/* عنصر VisualRequests مع القائمة الفرعية */}
        <li className="flex items-center justify-between px-4 py-2 cursor-pointer hover:bg-blue-600 rounded relative">
          <div className="flex items-center space-x-2 text-lg font-semibold">
            <DocumentIcon className="h-5 w-5 text-white" />
            <span>Visual Requests</span>
          </div>
          <CollapseButton
            isOpen={openList === "visualrequest"}
            setIsOpen={() => handleListClick("visualrequest")}
            Orientation={"vertical"}
          />
          <ul
            className={`absolute left-full top-0 ml-2 mt-1bg-blue-500 shadow-lg rounded-lg w-48 overflow-hidden transition-max-height duration-300 ease-in-out ${
              openList === "visualrequest" ? "max-h-96" : "max-h-0"
            }`}
          >
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Group Change Request</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Specialty Change Request</a></li>
            <li className="hover:bg-blue-600 p-2 rounded"><a href="">Club Creation Request</a></li>
          </ul>
        </li>
        
        {/* عنصر Additional Requests */}
        <li className="flex items-center justify-between px-4 py-2 cursor-pointer hover:bg-blue-600 rounded relative">
          <div className="flex items-center space-x-2 text-lg font-semibold">
            <DocumentIcon className="h-5 w-5 text-white" />
            <span>Additional Requests</span>
          </div>
          <CollapseButton
            isOpen={openList === "AdditionalRequests"}
            setIsOpen={() => handleListClick("AdditionalRequests")}
            Orientation={"vertical"}
          />
          <ul
            className={`absolute left-full top-0 ml-2 mt-1 bg-blue-500 shadow-lg rounded-lg w-48 overflow-hidden transition-max-height duration-300 ease-in-out ${
              openList === "AdditionalRequests" ? "max-h-96" : "max-h-0"
            }`}
          >
            <li className="hover:bg-blue-600 bg-blue-500 p-2 rounded"><a href="">Scholarship Request</a></li>
            <li className="hover:bg-blue-600 bg-blue-500 p-2 rounded"><a href="">Study Abroad Scholarship Request</a></li>
          </ul>
        </li>

        <li className=" bg-blue-500 hover:bg-blue-600 p-2 rounded pl-11"><a href="">View Demand Processing</a></li>
        <li className="bg-blue-500 hover:bg-blue-600 p-2 rounded pl-11"><a href="">Manage Student Accounts</a></li>
        <li className="bg-blue-500 hover:bg-blue-600 p-2 rounded pl-11"><a href="">Detailed Requests Display</a></li>
        <li className=" bg-blue-500 hover:bg-blue-600 p-2 rounded pl-11"><a href="">Report Problem</a></li>
        <li className=" bg-blue-500 hover:bg-blue-600 p-2 rounded pl-11"><a href="">Announcements</a></li>
      </ul>
    </div>
  );
}
