// ToggleButton.js
import React from "react";
import { ChevronLeftIcon, ChevronDownIcon, ChevronUpIcon, DocumentIcon } from "@heroicons/react/24/solid";

export default function CollapseButton({ isOpen, setIsOpen, ButtonIcon1, ButtonIcon2 }) {
  // دالة التبديل بين الحالات
  const handleClick = () => setIsOpen(!isOpen);

  return (
    <button
      className="p-2 bg-gray-700 hover:bg-gray-600 focus:outline-none rounded"
      onClick={handleClick}
    >
      {isOpen ? (
        <ButtonIcon1 className="h-5 w-5 text-white" />
      ) : (
        <ButtonIcon2 className="h-5 w-5 text-white" />
      )}
    </button>
  );
}
