// ToggleButton.js
import React from "react";
import { ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/24/solid"; // استيراد أيقونات السهم

export default function SubMenuButton({ isOpen, SidebarButton }) { // تلقي isOpen و toggleSidebar كـ props
  return (
    <button
      className="p-2 bg-gray-700 hover:bg-gray-600 focus:outline-none rounded" // ضبط تصميم الزر ليشبه الزر الأول
      onClick={ SidebarButton} // استدعاء دالة التبديل عند النقر
    >
      {isOpen ? (
        <ChevronLeftIcon className="h-5 w-5 text-white" /> // أيقونة السهم للإغلاق
      ) : (
        <ChevronRightIcon className="h-5 w-5 text-white" /> // أيقونة السهم للفتح
      )}
    </button>
  );
}
