import React, { useState } from 'react';

function ToggleButton() {
  const [isStudent, setIsStudent] = useState(true);

  return (
    <div className="flex items-center justify-center bg-white w-8 h-2">
      <div className="flex border rounded-lg overflow-hidden">
        <button onClick={() => setIsStudent(true)} className={`${isStudent ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}>Student</button>
        <button onClick={() => setIsStudent(false)} className={`text- ${!isStudent ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}>Administration</button>
      </div>
    </div>
  );
}

export default ToggleButton;
