// ToggleButton.js
import React from "react";
import { ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/24/solid"; // استيراد أيقونات السهم من Heroicons v2

export default function SubNebuButton({ isOpen, toggleSidebar }) { // تلقي isOpen و toggleSidebar كـ props
  return (
    <button
      className="p-2 bg-gray-700 hover:bg-gray-600 focus:outline-none"
      onClick={toggleSidebar} // استدعاء دالة التبديل عند النقر
    >
      {isOpen ? (
        <ChevronLeftIcon className="h-5 w-5 text-white" /> // أيقونة السهم لإغلاق الشريط
      ) : (
        <ChevronRightIcon className="h-5 w-5 text-white" /> // أيقونة السهم لفتح الشريط
      )}
    </button>
  );
}
