import React, { useState } from 'react';
import LabelStyle1 from './labels/LabelStyle1';

function ToggleButton() {
  const [isStudent, setIsStudent] = useState(true);

  return (
    <div className='flex justify-end'>
      <LabelStyle1 labelText='Administration'/>
      <div className="flex w-[80px] h-[25px] mx-[5px]">
        <div className={`flex border rounded-2xl h-full w-full relative ${isStudent ? 'bg-blue-300' : 'bg-orange-500'}`}>
          <div className={`absolute w-[28px] h-[23px] bg-blue-500 rounded-full transition-transform duration-300 ${isStudent ? 'left-0' : 'right-0'}`}/>        
          <button onClick={() => setIsStudent(true)} className={`flex-1`}/>
          <button onClick={() => setIsStudent(false)} className={`flex-1`}/>
        </div>
      </div>
      <LabelStyle1 labelText='Student' className="flex-1 justify-center"/>
    </div>
  );
}

export default ToggleButton;
