import React from "react";
import Login_SingUp_image from "../Images/Login_Singup_image.jpg"
import ToggleButton from "../components/custom controls/ToggleButton"
import LabelStyle1 from "../components/custom controls/labels/LabelStyle1";
import TextBoxStyle1 from "../components/custom controls/textBox/TextBoxStyle1"
import TextBoxStyle2 from "../components/custom controls/textBox/TextBoxStyle2"
import ButtonStyle1 from "../components/custom controls/buttons/ButtonStyle1"

export default function Login_SingUp(){
    return(
        <div className="flex items-center justify-center h-screen bg-white">
            <div className="grid grid-cols-2 w-[50%] h-[500px] bg-white rounded-lg shadow-md rounded-lg">
                {/*image section*/}
                <div>
                    <img src={Login_SingUp_image} alt="Univ-Boumerdes-Image" className="w-full h-full object-fill rounded-l-lg"/>
                </div>
                {/* Login and sing up section */}
                <div className="flex flex-col ml-10">
                    {/* Student_adminstation switch and welcom part */}
                        <LabelStyle1 labelText="Login" labelClassName="flex flex-auto justify-center mt-5 text-3xl mb-10"/>
                    {/* Login_singUP form */}
                    <form className="bg-white flex flex-col ml-5 w-[80%]">
                    <LabelStyle1 labelText="Email:" labelClassName="text-md"/>
                    <TextBoxStyle2 placholder="Example@gmail.com " textBoxClassName="w-full"/>
                    <LabelStyle1 labelText="Password:" labelClassName="text-md mt-5"/>
                    <TextBoxStyle2 placholder="password" textBoxClassName="w-full"/>
                    <a href="#" className="text-sm text-blue-400 flex justify-end">Forgot password</a>
                    <ButtonStyle1 buttonText="Login" buttonClassName="mt-5"/>
                    </form>
                    <div className="relative mt-8">
    <h5 className="absolute left-1/2 transform -translate-x-1/2 top-0 bg-white px-2">OR</h5> {/* تغيير الموقع ليكون فوق hr */}
    <hr className="border border-gray-500" />
</div>
                    <ToggleButton toggleButtonClassName="flex flex-auto justify-center items-end mb-3" leftLabel="Administration" rightLabel="Student"/>
                </div>
            </div>
        </div>
    );
}
