import React, { useState } from "react";

function App() {
  const [isInRed, setIsInRed] = useState(true);

  // تغيير موقع المستطيل الرمادي عند النقر على الزر
  const handleToggle = () => {
    setIsInRed(!isInRed);
  };

  return (
    <div className="flex items-center justify-center h-screen space-x-4">
      {/* المستطيل الأحمر */}
      <div className="relative w-32 h-32 bg-red-500 flex items-center justify-center">
        {isInRed && (
          <div className="absolute w-24 h-24 bg-gray-500 transition-transform duration-500">

          </div>
        )}
      </div>

      {/* زر التبديل */}
      <button
        onClick={handleToggle}
        className="p-2 bg-green-500 text-white rounded"
      >
        Toggle Move
      </button>

      {/* المستطيل الأزرق */}
      <div className="relative w-32 h-32 bg-blue-500 flex items-center justify-center">
        {!isInRed && (
          <div className="absolute w-24 h-24 bg-gray-500 transition-transform duration-500"></div>
        )}
      </div>
    </div>
  );
}

export default App;
