// ToggleButton.js
import React from "react";
import { ChevronLeftIcon, ChevronDownIcon, ChevronUpIcon, ChevronRightIcon } from "@heroicons/react/24/solid";

export default function CollapseButton({ isOpen, setIsOpen, Orientation }) {
  // دالة التبديل بين الحالات
  const handleClick = () => setIsOpen(!isOpen);

  return (
    <button
      className="p-2 bg-gray-700 hover:bg-gray-600 focus:outline-none rounded"
      onClick={handleClick}
    >
      {Orientation === "horizontal" ? (
        isOpen ? (
          <ChevronLeftIcon className="h-5 w-5 text-white" /> // أيقونة السهم للإغلاق (أفقي)
        ) : (
          <ChevronRightIcon className="h-5 w-5 text-white" /> // أيقونة السهم للفتح (أفقي)
        )
      ) : (
        isOpen ? (
          <ChevronUpIcon className="h-5 w-5 text-white" /> // أيقونة السهم للإغلاق (عمودي)
        ) : (
          <ChevronDownIcon className="h-5 w-5 text-white" /> // أيقونة السهم للفتح (عمودي)
        )
      )}
    </button>
  );
}
