import React from "react";
import Login_SingUp_bg from "../Images/Login_SingUp_bg.png"
import Login_SingUp_image from "../Images/Login_Singup_image.jpg"
import Login from "../components/Login";
import SingUp from "../components/SingUp";
import { useState } from "react";


export default function Login_SingUp(){
    const [position, setPosition] = useState(0);

  const toggleBox = () => {
    setPosition((prevPosition) => (prevPosition === 0 ? 1 : 0));
  };
    return(
        <div className="flex items-center justify-center h-screen bg-cover bg-center" style={{ backgroundImage: `url(${Login_SingUp_bg})`  }}>
            <div className="grid grid-cols-1 sm:grid-cols-2 w-[95%] md:w-[80%] lg:w-[60%] h-[500px] bg-white rounded-lg shadow-md">
                <div className={`hidden sm:block absolute transform transition-transform duration-500 ease-in-out w-[95%] md:w-[80%] lg:w-1/2 h-[500px] bg-blue-500 rounded shadow-lg ${
                                    position === 0 ? 'translate-x-0' : 'translate-x-44'
                                }`}>
                    <img src={Login_SingUp_image} alt="Univ-Boumerdes-Image" className="w-full h-full object-fill rounded-l-lg"/>
                </div>
                <SingUp />
                <Login />
            </div>
        </div>
    );
}
