// SideBar.js
import React, { useState } from "react";
import CollapseButton from "./custom controls/buttons/CollapseButton";// استيراد زر التبديل
import { ChevronLeftIcon, ChevronRightIcon ,ChevronDownIcon, ChevronUpIcon } from "@heroicons/react/24/solid";




export default function SideBar() {
  const [isOpen, setIsOpen] = useState(true); // حالة للتحكم في فتح وإغلاق الشريط الجانبي

  const SidebarButton = () => { // دالة لتغيير حالة الشريط الجانبي
    setIsOpen(isOpen => !isOpen);
  };

  return (
    <div className={`flex flex-col h-screen ${isOpen ? "w-64" : "w-16"} bg-gray-800 text-white transition-width duration-300`}>
      <CollapseButton ButtonIcon1={ChevronLeftIcon } ButtonIcon2={ChevronRightIcon} isOpen={isOpen} SidebarButton={SidebarButton} /> 
      {/* تمرير isOpen و SidebarButton كـ props لزر التبديل */}

      <ul className={`flex flex-col mt-4 space-y-2 ${!isOpen && "hidden"}`}>
        <li className="text-center">
          <div>
          <a href="#home" className="block py-2 px-4 hover:bg-gray-600 rounded">DocumentRequests</a>
          <ChevronLeftIcon className="h-5 w-5 text-white" />
          </div>
        </li>
       
      </ul>
    </div>
  );
}
