import React, { useState } from "react";

function ToggleButton() {
  const [isOn, setIsOn] = useState(false);

  const toggle = () => {
    setIsOn(!isOn);
  };

  return (
    <button
      onClick={toggle}
      className={`w-[30px] h-[10px] rounded-full transition-colors duration-300 ${
        isOn ? "bg-green-500" : "bg-gray-300"
      }`}
    >
      <span
        className={`block w-1/2 h-full rounded-full bg-white transform transition-transform duration-300 ${
          isOn ? "translate-x-full" : "translate-x-0"
        }`}
      />
    </button>
  );
}

export default ToggleButton;
