import React, { useState } from 'react';

function ToggleButton() {
  const [isToggled, setIsToggled] = useState(false);

  const handleToggle = () => {
    setIsToggled(!isToggled);
  };

  return (
    <div className="flex items-center justify-center h-screen">
      
      <label className="inline-flex items-center cursor-pointer">
        
        <input 
          type="checkbox" 
          className="sr-only" 
          checked={isToggled} 
          onChange={handleToggle} 
        />
        
        <div className={`relative w-14 h-8 bg-gray-300 rounded-full transition duration-300 ${isToggled ? 'bg-blue-500' : ''}`}>
          
          <div className={`absolute left-1 top-1 w-6 h-6 bg-white rounded-full transition duration-300 transform ${isToggled ? 'translate-x-full' : ''}`}></div>
        
        </div>
        
        <span className="ml-3 text-sm font-medium text-gray-900">
          {isToggled ? 'On' : 'Off'}
        </span>
      
      </label>
    
    </div>
  );
}

export default ToggleButton;
