import React, { useState } from 'react';

function SlideBox() {
  const [position, setPosition] = useState(0);

  const toggleBox = () => {
    setPosition((prevPosition) => (prevPosition === 0 ? 1 : 0));
  };

  return (
    <div className="flex flex-col items-center space-y-4">
      <button 
        onClick={toggleBox} 
        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition">
        Toggle Slide
      </button>
      
      {/* المربعات الثلاثة */}
      <div className="relative flex space-x-4">
        
        {/* المربع الأول */}
        <div className="w-40 h-40 bg-gray-300 rounded shadow-lg" />
        
        {/* المربع الثاني */}
        <div className="w-40 h-40 bg-gray-300 rounded shadow-lg" />
        
        {/* المربع الأساسي */}
        <div
          className={`absolute transform transition-transform duration-500 ease-in-out ${
            position === 0 ? 'translate-x-0' : 'translate-x-44'
          } w-40 h-40 bg-blue-500 rounded shadow-lg`}
        />
      </div>
    </div>
  );
}

export default SlideBox;
