import React, { useState } from 'react';

function ToggleButton() {
  const [isStudent, setIsStudent] = useState(true);

  return (
    <div className="grid grid-cols-2 overflow-hidden rounded-lg bg-white w-[30px] h-[10px]">
      
      <button 
        onClick={() => setIsStudent(true)} 
        className={`relative flex items-center justify-center px-4 py-2 ${isStudent ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}
      >
        {isStudent && (
          <span className="absolute left-2 w-2 h-2 bg-white rounded-full" />
        )}
        
      </button>
      
      <button 
        onClick={() => setIsStudent(false)} 
        className={`relative flex items-center justify-center px-4 py-2 ${!isStudent ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}
      >
        {!isStudent && (
          <span className="absolute left-2 w-2 h-2 bg-white rounded-full" />
        )}
        
      </button>
    
    </div>
  );
}

export default ToggleButton;
