// SideBar.js
import React, { useState } from "react";
import SubMenuButton from "./custom controls/buttons/SubMenuButton"; // استيراد زر التبديل

export default function SideBar() {
  const [isOpen, setIsOpen] = useState(true); // حالة للتحكم في فتح وإغلاق الشريط الجانبي

  const SidebarButton = () => { // دالة لتغيير حالة الشريط الجانبي
    setIsOpen(isOpen => !isOpen);
  };

  return (
    <div className={`flex flex-col h-screen ${isOpen ? "w-64" : "w-16"} bg-gray-800 text-white transition-width duration-300`}>
      <SubMenuButton isOpen={isOpen} SidebarButton={SidebarButton} /> 
      {/* تمرير isOpen و SidebarButton كـ props لزر التبديل */}

      <ul className={`flex flex-col mt-4 space-y-2 ${!isOpen && "hidden"}`}>
        <li className="text-center">
          <a href="#home" className="block py-2 px-4 hover:bg-gray-600 rounded">لا</a>
        </li>
       
      </ul>
    </div>
  );
}
