// ToggleButton.js
import React , { useState }from "react";
import { ChevrftIcononLe,ChevronDownIcon, ChevronUpIcon, DocumentIcon } from "@heroicons/react/24/solid";

export default function CollapseButton({isOpen,setIsOpen,Orientation}) { // تلقي isOpen و SidebarButton كـ props
  const ClickButtonHandled=()=>{ChangeState(!isOpen);}

  
  return (
    <button
      className="p-2 bg-gray-700 hover:bg-gray-600 focus:outline-none rounded" // ضبط تصميم الزر ليشبه الزر الأول
      onClick={ClickButtonHandled} // استدعاء دالة التبديل عند النقر
    >
      {isOpen ? (
        <ButtonIcon1  className="h-5 w-5 text-white" /> // أيقونة السهم للإغلاق
      ) : (
        <ButtonIcon2 className="h-5 w-5 text-white" /> // أيقونة السهم للفتح
      )}
    </button>
  );
}
